mapper.select:
select o.ord_id, c.clnt_cd, c.clnt_nm, o.order_dt as sOdate, o.dlvry_dt as eDdate, u.id, u.name as userNm, o.ord_num as ordNum from order_mng o join client c on o.clnt_id = c.clnt_id join user u on o.user_id = u.user_id
where clnt_nm like CONCAT('%', #{clntNm}, '%')
and o.order_dt between #{sOdate} and #{eOdate}
and o.order_dt >= #{sOdate} and o.order_dt <= #{eOdate}
and o.dlvry_dt between #{sDdate} and #{eDdate}
and o.dlvry_dt >= #{sDdate} and o.dlvry_dt <= #{eDdate}
and name like CONCAT('%', #{userNm}, '%')
order by sOdate desc limit #{startRow}, #{pageSize}

select
count(*)
from order_mng o join client c
on o.clnt_id = c.clnt_id
join user u
on o.user_id = u.user_id


where clnt_nm like CONCAT('%', #{clntNm}, '%')

 and o.order_dt between #{sOdate} and #{eOdate}

 and o.order_dt >= #{sOdate}
 and o.order_dt <= #{eOdate}

 and o.dlvry_dt between #{sDdate} and #{eDdate}

 and o.dlvry_dt >= #{sDdate}
 and o.dlvry_dt <= #{eDdate}

and
name like CONCAT('%', #{userNm}, '%')

select o.ord_id, o.ord_num as ordNum, c.clnt_nm,o.order_dt as sOdate, u.name as userNm, i.item_num, i.item_nm,i.invnt_unit, o.dlvry_dt as sDdate, o.ord_qty, o.cmplt_yn as cmpltYn from client c join order_mng o on o.clnt_id = c.clnt_id join item i on o.item_id = i.item_id join user u on o.user_id= u.user_id
where clnt_nm like CONCAT('%', #{clntNm}, '%')
and o.order_dt = #{sOdate} and o.dlvry_dt = #{sDdate}
and item_num like CONCAT('%', #{itemNum}, '%')
and item_nm like CONCAT('%', #{itemNm}, '%')
and name like CONCAT('%', #{userNm}, '%')
and o.cmplt_yn = #{cmpltYn}
order by sOdate desc limit #{startRow}, #{pageSize}

select count(*) from client c join order_mng o on o.clnt_id = c.clnt_id join item i on o.item_id = i.item_id join user u on o.user_id= u.user_id
where clnt_nm like CONCAT('%', #{clntNm}, '%')
and o.order_dt = #{sOdate} and item_num like CONCAT('%', #{itemNum}, '%')
and name like CONCAT('%', #{userNm}, '%')
and o.dlvry_dt = #{sDdate}
and o.cmplt_yn = #{cmpltYn}

select item_id as itemId, item_num as itemNum, item_nm as itemNm, invnt_unit as invntUnit from item
where item_num like CONCAT('%', #{itemNum}, '%') and item_nm like CONCAT('%', #{itemNm}, '%')
order by itemNum desc limit #{startRow}, #{pageSize}

select user_id as userId, id as userNum, name as userNm from user
where id like CONCAT('%', #{userNum}, '%') and name like CONCAT('%', #{userNm}, '%')
order by id desc limit #{startRow}, #{pageSize}

select clnt_cd as clntCd, clnt_nm as clntNm, clnt_id as clntId from client
where clnt_cd like CONCAT('%', #{clntCd}, '%') and clnt_nm like CONCAT('%', #{clntNm}, '%')
order by clnt_cd desc limit #{startRow}, #{pageSize}
