mapper.select:
select c.clnt_cd, c.clnt_nm, o.order_dt, o.dlvry_dt, u.id,u.name,o.csmtr_ord_num from client c join order_mng o on c.clnt_id = o.clnt_id join user u on o.user_id = u.user_id join item i on c.clnt_id = i.clnt_id where clnt_nm like CONCAT('%', #{clntNm}, '%')
and o.order_dt between #{sOdate} and #{eOdate}
and o.order_dt >= #{sOdate}
and o.order_dt <= #{eOdate}
and i.item_nm like CONCAT('%', #{itemName}, '%') order by c.clnt_id desc limit #{startRow}, #{pageSize}

select item_num as itemNum, item_nm as itemName from item
where item_num like CONCAT('%', #{itemNum}, '%') and item_nm like CONCAT('%', #{itemName}, '%')
order by item_num desc limit #{startRow}, #{pageSize}

select id as userNum, name as userName from user
where id like CONCAT('%', #{userNum}, '%') and name like CONCAT('%', #{userName}, '%')
order by id desc limit #{startRow}, #{pageSize}

select clnt_cd as clntCd, clnt_nm as clntName from client
where clnt_cd like CONCAT('%', #{clntCd}, '%') and clnt_nm like CONCAT('%', #{clntName}, '%')
order by clnt_cd desc limit #{startRow}, #{pageSize}
