mapper.select:
    	select sh.ship_id,c.clnt_cd,c.clnt_nm,sh.dlvry_dt as Dlvdate, sh.ship_dt as Shdate,
    	u.id, u.name as userNm,sh.ship_num
    	from ship_mng sh join client c
    	on sh.clnt_id = c.clnt_id
    	join user u
    	on sh.user_id = u.user_id
    	
    	where clnt_nm like CONCAT('%', #{clntNm}, '%')
    
    	 and sh.dlvry_dt between #{Dlvdate} and #{eDlvdate}
		
		 and sh.dlvry_dt >= #{Dlvdate}
		 and sh.dlvry_dt <= #{eDlvdate}

		 and sh.ship_dt between #{sshdate} and #{Shdate}
		
		 and sh.ship_dt >= #{sshdate}
		 and sh.ship_dt <= #{Shdate}

		and
		name like CONCAT('%', #{userNm}, '%')

		order by Dlvdate desc
		limit #{startRow}, #{pageSize}

    select count (*)
    from ship_mng sh join client c
    on sh.clnt_id = c.clnt_id
    join user u
    on sh.user_id = u.user_id
    
    
    where clnt_nm like CONCAT('%', #{clntNm}, '%')
    
     and o.order_dt between #{Dlvdate} and #{eDlvdate}
		
		 and sh.dlvry_dt >= #{Dlvdate}
		 and sh.dlvry_dt <= #{eDlvdate}

		 and o.dlvry_dt between #{sshdate} and #{Shdate}
		
		 and sh.ship_dt >= #{sshdate}
		 and sh.ship_dt <= #{Shdate}

		and
		name like CONCAT('%', #{userNm}, '%')

select sh.ship_id,sh.ship_num,c.clnt_nm,sh.dlvry_dt as Dlvdate, u.name as userNm,i.item_num, i.item_nm,i.invnt_unit, sh.ship_dt as Shdate,sh.ship_qty from client c join ship_mng sh on sh.clnt_id = c.clnt_id join item i on sh.item_id = i.item_id join user u on sh.user_id = u.user_id
where clnt_nm like CONCAT('%', #{clntNm}, '%')
and sh.dlvry_dt = #{Dlvdate} and sh.ship_dt = #{Shdate}
and item_num like CONCAT('%', #{itemNum}, '%')
and item_nm like CONCAT('%', #{itemNm}, '%')
and name like CONCAT('%', #{userNm}, '%')
and sh.cmplt_yn = #{cmpltYn}
order by Dlvdate desc limit #{startRow}, #{pageSize}

select item_id as itemId, item_num as itemNum, item_nm as itemNm, invnt_unit as invntUnit from item
where item_num like CONCAT('%', #{itemNum}, '%') and item_nm like CONCAT('%', #{itemNm}, '%')
order by itemNum desc limit #{startRow}, #{pageSize}

select user_id as userId, id as userNum, name as userNm from user
where id like CONCAT('%', #{userNum}, '%') and name like CONCAT('%', #{userNm}, '%')
order by id desc limit #{startRow}, #{pageSize}

select clnt_cd as clntCd, clnt_nm as clntNm, clnt_id as clntId from client
where clnt_cd like CONCAT('%', #{clntCd}, '%') and clnt_nm like CONCAT('%', #{clntNm}, '%')
order by clnt_cd desc limit #{startRow}, #{pageSize}

mapper.update:
update ship_mng
set clnt_id = #{clntId}, dlvry_dt = #{dlvryDt}, user_id = #{userId}, ship_dt = #{shipDt},
		item_id = #{itemId}, ship_qty = #{shipQty}, update_id = #{updateId}, update_dt = #{updateDt}
		where ship_id = #{shipId}
